mapper.select:

SELECT count(*) FROM a_noticeboard where notice_code > 0


and writeDate >= #{write_startDate}



and writeDate <= #{write_endDate} 



and notice_type = #{notice_type} 


and notice_title = #{notice_title}


		 select * from 
        (select @rownum := @rownum + 1 as rn, a_id, notice_title, writeDate, post_chennel, notice_startDate, notice_endDate, notice_type, notice_code, post_check 
        from a_noticeboard, (select @rownum := 0)  as rowcolumn order by writeDate desc) s
		where rn >= #{startNO} and rn <= #{endNO}


        and writeDate >= #{write_startDate}



        and writeDate <= #{write_endDate} 



        and notice_type = #{notice_type}


        and notice_title like concat('%',#{notice_title},'%')
