mapper.select:

select * 
from (
select (@ROWNUM := @ROWNUM + 1) rn, c.* 
from (
select * 
from h_host 
where h_agree_1 = 'Y'


and h_id like concat('%',#{h_id2},'%')


and joinDate >= #{join_startDate}



and joinDate <= #{join_endDate}



and h_name like concat('%',#{h_name},'%')


and del_yn = #{h_del_yn}


and h_sellerNum like concat('%',#{h_sellerNum},'%') 


order by joinDate desc
)c,(select @ROWNUM := 0)s)b  
where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

        SELECT count(*) FROM h_host where h_agree_1 = 'Y'

		 and h_id like concat('%',#{h_id2},'%')


		 and joinDate >= #{join_startDate}



		 and joinDate <= #{join_endDate}



		 and h_name like concat('%',#{h_name},'%')


		 and del_yn = #{h_del_yn}


		 and h_sellerNum like concat ('%',#{h_sellerNum},'%')


		 select * 
		 from (
		 select (@ROWNUM := @ROWNUM + 1) rn, c.*
		 from (
		 select H.h_id, H.h_name, I.hostInfo_name, I.creDate, I.h_sellerNum, I.host_type, I.del_yn, I.roadAddress, I.h_code  
		 from h_host_Info I
		 inner join h_host H
         on I.h_id = H.h_id
		 where I.h_code > 0


		 and H.h_id like concat('%',#{h_id2},'%')


		 and I.creDate >= #{join_startDate}



		 and I.creDate <= #{join_endDate}



		 and H.h_name like concat('%',#{h_name},'%')


		 and I.del_yn = #{h_del_yn}


		 and I.h_sellerNum like concat('%',#{h_sellerNum},'%') 


		 order by creDate desc
		 )c,(select @ROWNUM := 0)s)b  
		 where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

        SELECT count(*) 
        from h_host_Info I
		inner join h_host H
        on I.h_id = H.h_id where I.h_code > 0

		 and h_id like concat('%',#{h_id2},'%')


		 and creDate >= #{join_startDate}



		 and creDate <= #{join_endDate}



		 and h_name like concat('%',#{h_name},'%')


		 and del_yn = #{h_del_yn}


		 and h_sellerNum like concat ('%',#{h_sellerNum},'%')

		 select * from (
		select @ROWNUM := @ROWNUM + 1 as recNum, b.* from (select (@ROWNUM := 0)) a,
		(select * from h_host where h_agree_1 = 'Y' 

        		and joinDate >= #{join_startDate}



        		and joinDate <= #{join_endDate} 



        		and del_yn = #{del_yn} 


        		and h_id like concat('%',#{h_id},'%') 


        		and h_name like concat('%',#{h_name},'%') 


        		and h_sellerNum like concat('%',#{h_sellerNum},'%') 

        order by joinDate desc
		)b
		)c
		where recNum between (#{section}-1)*100+(#{pageNum}-1)*10+1 and
		(#{section}-1)*100+(#{pageNum})*10
