mapper.select:
select COUNT(*) as cnt from ( select U.*, I.h_name, Y.title, G.room_imageName from u_cart U, h_host I, h_host_info F, h_room Y, h_roomimage G where U.u_id=#{u_id} and F.h_code=U.h_code and F.h_id= I.h_id and Y.room_code = U.room_code and G.room_code = U.room_code and G.room_imageType= 'main_image'
and U.checkIn_date between #{checkIn_date} and #{checkOut_date}
order by creDate desc
) R

mapper.insert:
insert into u_cart (u_id, h_code, room_code, checkIn_date, checkOut_date, people_count, room_fee, discount, total
)
values (#{u_id}, #{h_code}, #{room_code}, #{checkIn_date}, #{checkOut_date}, #{people_count}, #{room_fee}, #{discount}, #{total} )
